SELECT value,nc1.peroid_time,nc1.project_id from v_norm_constant nc1 where (nc1.project_id=#{projectId} or nc1.project_id=0) and nc1.peroid_time= (SELECT max(peroid_time) from v_norm_constant nc where (nc.project_id=#{projectId} or nc.project_id=0)
and nc.peroid_time<=#{endTime,jdbcType=TIMESTAMP}
and nc.code_number=#{code_number}) ORDER BY nc1.peroid_time DESC

SELECT v0.id,v0.`name` ,sum((SELECT VALUE FROM v_norm_constant nc1 WHERE ( nc1.project_id = v0.id OR nc1.project_id = 0 ) AND nc1.peroid_time = ( SELECT max(peroid_time) FROM v_norm_constant nc WHERE ( nc.project_id = v0.id OR nc.project_id = 0 )
and nc.peroid_time<=#{endTime}
AND nc.code_number = #{codeNumber} ))) as VALUE
from project v0 where v0.id in (${projects})

